// src/components/HouseCard.js
import React from 'react';
import './HouseCard.css';
import { Swiper, SwiperSlide } from 'swiper/react';  // Import Swiper components
import { Pagination } from 'swiper/modules';  // Import only Pagination module
import 'swiper/css';  // Swiper core styles
import 'swiper/css/pagination';  // Swiper Pagination styles

// FontAwesome imports
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt, faHouseUser } from '@fortawesome/free-solid-svg-icons';

function HouseCard({ house }) {
  return (
    <div className="house-card">
      {/* Image Slideshow using Swiper */}
      <div className="house-images">
        <Swiper
          modules={[Pagination]}  // Enable only the Pagination module, no Navigation
          spaceBetween={10}
          slidesPerView={1}
          pagination={{ clickable: true }}  // Enable clickable pagination dots
        >
          {house.images.map((image, index) => (
            <SwiperSlide key={index}>
              <img src={image} alt={`Slide ${index}`} className="house-image" />
            </SwiperSlide>
          ))}
        </Swiper>
      </div>

      {/* House Price with Icon */}
      <p className="house-price">
        <FontAwesomeIcon icon={faHouseUser} className="price-icon" />  {/* Icon before the price */}
        {house.price}
      </p>

      {/* House Type */}
      <div className="house-type">{house.type}</div>

      {/* House Description */}
      <p className="house-description">{house.description}</p>

      {/* Location Section with FontAwesome Icon */}
      <div className="house-location">
        <FontAwesomeIcon icon={faMapMarkerAlt} className="location-icon" />  {/* Using FontAwesome */}
        <span>{house.location}</span>
      </div>

      {/* Action Buttons */}
      <div className="house-actions">
        <a href={`/house/${house.id}`} className="details-link">View Details</a>
        <button className="book-visit-btn">Book Visit</button>
      </div>
    </div>
  );
}

export default HouseCard;
