import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Houselisting.css';

const HouseListing = () => {
  const [houses, setHouses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch house data from your local API
    axios.get('http://localhost:3000/houses')
      .then(response => {
        setHouses(response.data);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching house data:', error);
        setError('Failed to load house data.');
        setLoading(false);
      });
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  if (houses.length === 0) {
    return <p>No houses available at the moment.</p>;
  }

  return (
    <div className="house-listing">
      {houses.map(house => (
        <div key={house.id} className="house-item">
          <div className="image-section">
            {/* Display images */}
            <div className="slideshow">
              {house.images.map((image, index) => (
                <img key={index} src={image} alt={`House ${house.id} - ${index}`} />
              ))}
            </div>
          </div>
          <div className="details-section">
            <h3>Price: ${house.price}</h3>
            <div className="description">
              <p>Type: {house.type}</p>
              <p>{house.description}</p>
            </div>
            <div className="actions">
              <a href={`/houses/${house.id}`} className="details-link">View Details</a>
              <button className="book-visit-button">Book Visit</button>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
};

export default HouseListing;
