import React, { useState, useEffect } from 'react';
import './HouseCard.css';
import { Swiper, SwiperSlide } from 'swiper/react';
import { Pagination } from 'swiper/modules';
import 'swiper/css';
import 'swiper/css/pagination';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt, faHouseUser, faHeart } from '@fortawesome/free-solid-svg-icons';
import BookVisitForm from './BookVisitForm'; // Ensure this is the correct path and import

function HouseCard({ house }) {
  const [isFavorite, setIsFavorite] = useState(false);
  const [showBookVisitForm, setShowBookVisitForm] = useState(false);

  // Check if the house is already in local storage when the component mounts
  useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];
    setIsFavorite(savedFavorites.some(favHouse => favHouse.id === house.id));
  }, [house.id]);

  // Function to handle adding/removing favorites
  const toggleFavorite = () => {
    const savedFavorites = JSON.parse(localStorage.getItem('favorites')) || [];

    if (isFavorite) {
      // Remove from favorites
      const updatedFavorites = savedFavorites.filter(favHouse => favHouse.id !== house.id);
      localStorage.setItem('favorites', JSON.stringify(updatedFavorites));
      setIsFavorite(false);
    } else {
      // Add to favorites
      localStorage.setItem('favorites', JSON.stringify([...savedFavorites, house]));
      setIsFavorite(true);
    }
  };

  // Function to open the Book Visit form
  const openBookVisitForm = () => {
    setShowBookVisitForm(true);
  };

  // Function to close the Book Visit form
  const closeBookVisitForm = () => {
    setShowBookVisitForm(false);
  };

  return (
    <div className={`house-card ${isFavorite ? 'highlight-favorite' : ''}`}>
      <div className="house-images">
        <Swiper modules={[Pagination]} spaceBetween={10} slidesPerView={1} pagination={{ clickable: true }}>
          {house.images.map((image, index) => (
            <SwiperSlide key={index}>
              <img 
                src={image} 
                alt={`Slide ${index}`} 
                className="house-image" 
                onError={(e) => { e.target.onerror = null; e.target.src = 'fallback-image-url.jpg'; }} // Add fallback image
              />
            </SwiperSlide>
          ))}
        </Swiper>
      </div>

      <div className="house-info">
        <div className="house-price">
          <FontAwesomeIcon icon={faHouseUser} className="price-icon" /> {house.price}
        </div>

        <div className="house-type">{house.type}</div>
        <p className="house-description">{house.description}</p>
        <div className="house-location">
          <FontAwesomeIcon icon={faMapMarkerAlt} className="location-icon" /> {house.location}
        </div>
      </div>

      <div className="house-actions">
        <a href={`/house/${house.id}`} className="details-link">View Details</a>
        <button className="book-visit-btn" onClick={openBookVisitForm}>Book Visit</button>
        <button 
          className={`favorite-btn ${isFavorite ? 'favorited' : ''}`} 
          onClick={toggleFavorite}
          aria-label={isFavorite ? 'Remove from Favorites' : 'Add to Favorites'} // Improved accessibility
        >
          <FontAwesomeIcon icon={faHeart} /> {isFavorite ? 'Remove from Favorites' : 'Add to Favorites'}
        </button>
      </div>

      {/* Show the BookVisitForm as a popup */}
      {showBookVisitForm && (
        <div className="book-visit-form-overlay">
          <div className="book-visit-form-container">
            <BookVisitForm closeForm={closeBookVisitForm} />
          </div>
        </div>
      )}
    </div>
  );
}

export default HouseCard;
