// src/components/HouseCard.js
import React from 'react';
import './HouseCard.css';

// FontAwesome imports
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMapMarkerAlt } from '@fortawesome/free-solid-svg-icons';

function HouseCard({ house }) {
  return (
    <div className="house-card">
      {/* Image Slideshow Placeholder */}
      <div className="house-images">
        <img src={house.images[0]} alt={house.type} className="house-image" />
      </div>

      {/* House Price */}
      <p className="house-price">{house.price}</p>

      {/* House Type */}
      <div className="house-type">{house.type}</div>



      {/* House Description */}
      <p className="house-description">{house.description}</p>

      {/* Location Section with FontAwesome Icon */}
      <div className="house-location">
        <FontAwesomeIcon icon={faMapMarkerAlt} className="location-icon" />  {/* Using FontAwesome */}
        <span>{house.location}</span>
      </div>

      {/* Action Buttons */}
      <div className="house-actions">

        <a href={`/house/${house.id}`} className="details-link">View Details</a>
        <button className="book-visit-btn">Book Visit</button>

      </div>
    </div>
  );
}

export default HouseCard;
