import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import RequestHouseForm from './RequestHouseForm'; // Import the RequestHouseForm component

function Navbar() {
  const [showRequestForm, setShowRequestForm] = useState(false);

  // Function to open the request form
  const openRequestForm = () => {
    setShowRequestForm(true);
  };

  // Function to close the request form
  const closeRequestForm = () => {
    setShowRequestForm(false);
  };

  return (
    <>
      <nav className="navbar">
        <div className="navbar-left">
          <img
            src="data:image/png;base64,"
            alt="Logo"
            className="navbar-logo"
          />
          <h1 className="navbar-title">Hounter</h1>
        </div>
        <div className="navbar-right">
          {/* Link to Favorites with a red heart */}
          <Link to="/favorites" className="navbar-button favorites-link">
            <FontAwesomeIcon icon={faHeart} />
          </Link>
          <button className="navbar-button" onClick={openRequestForm}>Request House</button>
        </div>
      </nav>

      {/* Show the RequestHouseForm as a popup */}
      {showRequestForm && <RequestHouseForm closeForm={closeRequestForm} />}
    </>
  );
}

export default Navbar;
